package com.jayfella.lemur.util;

import com.jme3.math.ColorRGBA;
import com.jme3.texture.Texture;
import com.simsilica.lemur.component.TbtQuadBackgroundComponent;

public class BackgroundComponents {

    private BackgroundComponents() {

    }

    public static TbtQuadBackgroundComponent gradient() {
        return gradient(ColorRGBA.White.clone());
    }

    public static TbtQuadBackgroundComponent gradient(ColorRGBA color) {

        Texture texture = new TextureUtils().fromBase64("");
        TbtQuadBackgroundComponent backgroundComponent = TbtQuadBackgroundComponent.create(texture, 1, 1, 1, 126, 126, 1, false);
        backgroundComponent.setColor(color);
        return backgroundComponent;
    }

    public static TbtQuadBackgroundComponent doubleGradient() {
        return doubleGradient(ColorRGBA.White.clone());
    }

    public static TbtQuadBackgroundComponent doubleGradient(ColorRGBA color) {

        Texture texture = new TextureUtils().fromBase64("");

        TbtQuadBackgroundComponent backgroundComponent = TbtQuadBackgroundComponent.create(texture, 1, 1, 1, 126, 126, 1, false);
        backgroundComponent.setColor(color);

        return backgroundComponent;
    }

}
