package com.jayfella.lemur.util;

import com.jme3.math.ColorRGBA;
import com.jme3.math.Vector2f;
import com.jme3.texture.Texture;
import com.simsilica.lemur.component.IconComponent;

public class IconComponents {

    public static IconComponent defaultCheckBoxIcon(boolean checked) {

        Texture texture = checked
                ? new TextureUtils().fromBase64("")
                : new TextureUtils().fromBase64("");

        ColorRGBA color = checked
                ? new ColorRGBA(0.5f, 0.9f, 0.9f, 0.9f)
                : new ColorRGBA(0.6f, 0.8f, 0.8f, 0.8f);

        IconComponent iconComponent = new IconComponent(texture, new Vector2f(1, 1), 5, 0, 1, false);
        iconComponent.setColor(color);

        return iconComponent;
    }

}
